import React, {Component} from 'react';



class Hello extends Component{

	state = {
    	company: []
   	}




 componentDidMount() {

        var xhr = new XMLHttpRequest();
        xhr.open("GET", 'http://localhost:8080/hello', true);
        xhr.onload = function (e) {
            if (xhr.readyState === 4) {
              if (xhr.status === 200) {
                    var json_obj = JSON.parse(xhr.responseText);
                    this.setState({company: json_obj });              
              }else {
                console.log(xhr.statusText + "ERROR");
              }
            }else{
              console.log("ERROR");
            }    

        }


        this.setState({company: [{"id":5,"name":"hola","rfc":"mundo"},{"id":6,"name":"luis","rfc":"zavaleta"}]});
     }





	render(){
		return (
    		<div className="App">
      			<h1> A {this.state.company.name} HRLL</h1>
    		</div>
  		);
	}
}



export default Hello;
